import React, { useState } from "react";




const App = () => {
    let time = new Date().toLocaleTimeString();

    const [ctime, setCtime] = useState(time);
    const updateTime = ()=>{
        time = new Date().toLocaleTimeString();
        setCtime(time);
    }
    setInterval(updateTime,);
    return(
    <>
    <h1>Time: {ctime}</h1>
    {/* <button onClick= {updateTime}>get time</button> */}
    </>
    );
};

export default App;
