import React, { useState } from "react";

const App = () => {
    const purple = "purple";
    const[bg, setBg] = useState(purple);
    const [name, setName] = useState("Click Me");
  
    const bgChange = ()=>{
        let newBg = "#23395e";
        setBg(newBg);
        
    };

    return(
    <React.Fragment>
        <div style= {{backgroundColor: bg}}>
            <button onClick = {bgChange}> {name}</button>
        </div>
    </React.Fragment>
    );
};

export default App;
